{"changed":true,"filter":false,"title":"usuario.php","tooltip":"/application/models/usuario.php","value":"<?php\nclass model_teste extends CI_Model{\n \n  private $nome;\n  private $descricao;\n \n  #constroi o objeto\n  public function __construct($nome='', $descricao=''){\n     if(isset($nome)) $this->name=$nome;\n     if(isset($descricao)) $this->description=$descricao;\n  }\n  \n  #insere um novo registro no banco\n  public function insert(){\n   return $this->db->insert('tabela_banco',$this);\n }\n  \n  #Remove um objeto de acordo com o nome\n  public function remove () {\n    return $this->db->delete('tabela_banco',array('nome'=>$this->nome));\n  }\n \n #Atualiza o objeto a partir do nome\n public function update ($nome='') {\n     $objeto['nome']=$nome;\n     //$this->db->update(nome da tabela,valores de atualização,referência)\n     return $this->db->update('tabela_banco',$this,$objeto);\n  }\n  \n #Retorna o objeto\n public function get($filtro='') {\n   //Adiciona clausula where\n   if(!empty($filtro)) $this->db->where('nome', $filtro);\n   return $this->db->get('tabela_banco');\n  }\n}\n","undoManager":{"mark":-2,"position":0,"stack":[[{"start":{"row":0,"column":0},"end":{"row":36,"column":0},"action":"insert","lines":["<?php","class model_teste extends CI_Model{"," ","  private $nome;","  private $descricao;"," ","  #constroi o objeto","  public function __construct($nome='', $descricao=''){","     if(isset($nome)) $this->name=$nome;","     if(isset($descricao)) $this->description=$descricao;","  }","  ","  #insere um novo registro no banco","  public function insert(){","   return $this->db->insert('tabela_banco',$this);"," }","  ","  #Remove um objeto de acordo com o nome","  public function remove () {","    return $this->db->delete('tabela_banco',array('nome'=>$this->nome));","  }"," "," #Atualiza o objeto a partir do nome"," public function update ($nome='') {","     $objeto['nome']=$nome;","     //$this->db->update(nome da tabela,valores de atualiz